ry>\n	<#assign\n		assetRenderer = entry.getAssetRenderer()\n\n		ddmFormValuesReader = assetRenderer.getDDMFormValuesReader()\n\n		ddmFormFieldValues = ddmFormValuesReader.getDDMFormFieldValues(\"ddm-geolocation\")\n\n		coordinatesJSONObjects = []\n	/>\n\n	<#list ddmFormFieldValues as ddmFormFieldValue>\n		<#assign\n			value = ddmFormFieldValue.getValue()\n\n			coordinatesJSONObject = jsonFactoryUtil.createJSONObject(value.getString(locale))\n\n			coordinatesJSONObjects = coordinatesJSONObjects + [coordinatesJSONObject]\n		/>\n	</#list>\n\n	<#list coordinatesJSONObjects as coordinatesJSONObject>\n		<#assign featureJSONObject = jsonFactoryUtil.createJSONObject() />\n\n		<@liferay.silently featureJSONObject.put(\"type\", \"Feature\") />\n\n		<#assign geometryJSONObject = jsonFactoryUtil.createJSONObject() />\n\n		<@liferay.silently geometryJSONObject.put(\"type\", \"Point\") />\n\n		<#assign coordinatesJSONArray = [coordinatesJSONObject.getDouble(\"longitude\"), coordinatesJSONObject.getDouble(\"latitude\")] />\n\n		<@liferay.silently geometryJSONObject.put(\"coordinates\", coordinatesJSONArray) />\n\n		<@liferay.silently featureJSONObject.put(\"geometry\", geometryJSONObject) />\n\n		<#assign propertiesJSONObject = jsonFactoryUtil.createJSONObject() />\n\n		<@liferay.silently propertiesJSONObject.put(\"title\", assetRenderer.getTitle(locale)) />\n\n		<#assign entryAbstract>\n			<@getAbstract asset=entry />\n		</#assign>\n\n		<@liferay.silently propertiesJSONObject.put(\"abstract\", entryAbstract) />\n\n		<#if stringUtil.equals(mapsAPIProvider, \"Google\")>\n			<#assign\n				images = {\n					\"com.liferay.document.library.kernel.model.DLFileEntry\": \"${themeDisplay.getProtocol()}://maps.google.com/mapfiles/ms/icons/green-dot.png\",\n					\"com.liferay.portlet.dynamicdatalists.model.DDLRecord\": \"${themeDisplay.getProtocol()}://maps.google.com/mapfiles/ms/icons/red-dot.png\",\n					\"com.liferay.journal.model.JournalArticle\": \"${themeDisplay.getProtocol()}://maps.google.com/mapfiles/ms/icons/blue-dot.png\",\n					\"default\": \"${themeDisplay.getProtocol()}://maps.google.com/mapfiles/ms/icons/yellow-dot.png\"\n				}\n			/>\n\n			<#if images?keys?seq_contains(entry.getClassName())>\n				<@liferay.silently propertiesJSONObject.put(\"icon\", images[entry.getClassName()]) />\n			<#else>\n				<@liferay.silently propertiesJSONObject.put(\"icon\", images[\"default\"]) />\n			</#if>\n		</#if>\n\n		<@liferay.silently featureJSONObject.put(\"properties\", propertiesJSONObject) />\n\n		<@liferay.silently featureJSONArray.put(featureJSONObject) />\n	</#list>\n</#list>\n\n<@liferay.silently featureCollectionJSONObject.put(\"features\", featureJSONArray) />\n\n<style type=\"text/css\">\n	.asset-entry-abstract {\n		min-width: 400px;\n	}\n\n	.leaflet-popup .asset-entry-abstract {\n		display: inline-block;\n	}\n\n	.asset-entry-abstract .asset-entry-abstract-image {\n		display: block;\n		float: left;\n		height: 128px;\n		margin-right: 1em;\n		text-align: center;\n	}\n\n	.asset-entry-abstract .asset-entry-abstract-image img {\n		display: block;\n		margin: 0 auto;\n		max-height: 100%;\n	}\n\n	.asset-entry-abstract .taglib-icon {\n		float: right;\n	}\n</style>\n\n<@liferay_map[\"map-display\"]\n	name=\'Map\'\n	points=\"${featureCollectionJSONObject}\"\n/>\n\n<@liferay_aui.script use=\"liferay-map-base\">\n	Liferay.componentReady(\'<@portlet.namespace />Map\').then(\n		function(map) {\n			map.on(\n				\'featureClick\',\n				function(event) {\n					var feature = event.feature;\n\n					map.openDialog(\n						{\n							content: feature.getProperty(\'abstract\'),\n							marker: feature.getMarker(),\n							position: feature.getGeometry().get(\'location\')\n						}\n					);\n				}\n			);\n		}\n	);\n</@liferay_aui.script>\n\n<#macro getAbstract asset>\n	<div class=\"asset-entry-abstract\" id=\"<@portlet.namespace />assetEntryAbstract\">\n		<#assign\n			showEditURL = paramUtil.getBoolean(renderRequest, \"showEditURL\", true)\n\n			assetRenderer = asset.getAssetRenderer()\n		/>\n\n		<#if showEditURL && assetRenderer.hasEditPermission(permissionChecker)>\n			<#assign\n				editPortletURL = assetRenderer.getURLEdit(renderRequest, renderResponse, windowStateFactory.getWindowState(\"NORMAL\"), themeDisplay.getURLCurrent())\n			/>\n\n			<@liferay_ui.icon\n				image=\"edit\"\n				label=true\n				message=\"edit\"\n				url=editPortletURL.toString()\n			/>\n		</#if>\n\n		<#if assetRenderer.getThumbnailPath(renderRequest)??>\n			<div class=\"asset-entry-abstract-image\">\n				<img src=\"${assetRenderer.getThumbnailPath(renderRequest)}\" />\n			</div>\n		</#if>\n\n		<#assign assetURL = assetPublisherHelper.getAssetViewURL(renderRequest, renderResponse, assetRenderer, asset, !stringUtil.equals(assetLinkBehavior, \"showFullContent\")) />\n\n		<div class=\"asset-entry-abstract-content\">\n			<h3><a href=\"${assetURL}\">${assetRenderer.getTitle(locale)}</a></h3>\n\n			<div>\n				${assetRenderer.getSummary(renderRequest, renderResponse)}\n			</div>\n		</div>\n\n		<div class=\"asset-entry-abstract-footer\">\n			<a href=\"${assetURL}\">${languageUtil.get(locale, \"read-more\")} &raquo;</a>\n		</div>\n	</div>\n</#macro>',0,20100,NULL,'2020-05-18 08:46:48.309000'),(0,0,34529,20123,20095,20100,NULL,'2020-05-18 08:46:48.791000',20003,0,34527,'1.0','<?xml version=\'1.0\' encoding=\'UTF-8\'?><root available-locales=\"en_US,sv_SE,pt_BR,ja_JP,fr_FR,hu_HU,de_DE,ca_ES,ar_SA,fi_FI,zh_CN,es_ES,nl_NL\" default-locale=\"en_US\"><Name language-id=\"sv_SE\">Utförlig sammanfattning</Name><Name language-id=\"pt_BR\">Sumário Rico</Name><Name language-id=\"ja_JP\">要約（文字数多め）</Name><Name language-id=\"fr_FR\">Sommaire enrichi</Name><Name language-id=\"hu_HU\">Bő összesítő</Name><Name language-id=\"de_DE\">Rich-Text-Zusammenfassung</Name><Name language-id=\"ca_ES\">Resum enriquit</Name><Name language-id=\"ar_SA\">ملخص غني</Name><Name language-id=\"fi_FI\">Muotoiltu yhteenveto</Name><Name language-id=\"en_US\">Rich Summary</Name><Name language-id=\"zh_CN\">丰富总结</Name><Name language-id=\"es_ES\">Resumen completo</Name><Name language-id=\"nl_NL\">Rijke samenvatting</Name></root>','<?xml version=\'1.0\' encoding=\'UTF-8\'?><root available-locales=\"en_US,sv_SE,pt_BR,ja_JP,fr_FR,hu_HU,de_DE,ca_ES,ar_SA,fi_FI,zh_CN,es_ES,nl_NL\" default-locale=\"en_US\"><Description language-id=\"sv_SE\">Visar sammanfattningar, ikoner, liknande tillgångar samt utskrifts- och redigeringsåtgärder för tillgångar. Valfritt inkluderar bokmärken och omdömen för tillgångar.</Description><Description language-id=\"pt_BR\">Exibe abstracts, ícones, conteúdos relacionados e ações de imprimir/editar para os conteúdos. Opcionalmente, inclui marcadores e classificações de conteúdos.</Description><Description language-id=\"ja_JP\">概要、アイコン、関連するアセット、アセットの印刷/編集を表示する。アセットのブックマークと評価も設定できます</Description><Description language-id=\"fr_FR\">Affiche les résumés, icons, les assets relatifs, et les actions imprimer/éditer pour les assets. Facultativement inclut les signets et les notations d\'assets.</Description><Description language-id=\"hu_HU\">Megjeleníti a kivonatokat, ikonokat, a kapcsolódó tartalmakat, és a nyomtatási/szerkesztési műveleteket a tarlamakhoz. Opcionálisan tartalmazza a tartalom könyvjelzőket és értékeléseket.</Description><Description language-id=\"de_DE\">Stellt Kurzfassungen, Icons, verwandte Assets sowie Druck- und Bearbeitungsoptionen für Assets dar. Kann auch Bookmarks und Bewertungen enthalten.</Description><Description language-id=\"ca_ES\">Mostra resums, icones, continguts relacionats i accions d\'impressió o edició per continguts. Opcionalment, inclou enllaços i valoracions.</Description><Description language-id=\"ar_SA\">يعرض الملخصات والأيقونات والأصول ذات الصلة وإجراءات الطباعة/التحرير للأصول. ويمكنك بشكل اختياري تضمين الإشارات المرجعية وتقييمات الأصول.</Description><Description language-id=\"fi_FI\">Näyttää lyhenteet, kuvakkeet, liittyvät sisällöt ja sisältöjen tulosta/muokkaa toiminnot. Voit ottaa mukaan myös sisältöjen kirjanmerkit ja arvostelut.</Description><Description language-id=\"en_US\">Displays abstracts, icons, related assets, and print/edit actions for assets. Optionally include asset bookmarks and ratings.</Description><Description language-id=\"zh_CN\">显示摘要、图标、相关资产以及资产的打印和编辑操作。可选择性包含资产书签和评分。</Description><Description language-id=\"es_ES\">Muestra abstractos, iconos, activos relacionados e imprime/edita acciones para activos. Opcionalmente incluye marcadores de activos y calificaciones.</Description><Description language-id=\"nl_NL\">Toon samenvattingen, pictogrammen, gerelateerde content en afdruk en bewerk acties voor content. Voeg optioneel bladwijzers en beoordelingen toe.</Description></root>','ftl','<#if !entries?has_content>\n	<#if !themeDisplay.isSignedIn()>\n		${renderRequest.setAttribute(\"PORTLET_CONFIGURATOR_VISIBILITY\", true)}\n	</#if>\n\n	<div class=\"alert alert-info\">\n		<@liferay_ui[\"message\"] key=\"there-are-no-results\" />\n	</div>\n</#if>\n\n<#list entries as entry>\n	<#assign\n		entry = entry\n\n		assetRenderer = entry.getAssetRenderer()\n\n		entryTitle = htmlUtil.escape(assetRenderer.getTitle(locale))\n\n		viewURL = assetPublisherHelper.getAssetViewURL(renderRequest, renderResponse, assetRenderer, entry, !stringUtil.equals(assetLinkBehavior, \"showFullContent\"))\n	/>\n\n	<div class=\"asset-abstract\">\n		<div class=\"float-right\">\n			<@getPrintIcon />\n\n			<@getFlagsIcon />\n\n			<@getEditIcon />\n		</div>\n\n		<h3 class=\"asset-title\">\n			<a href=\"${viewURL}\">\n				${entryTitle}\n			</a>\n		</h3>\n\n		<@getMetadataField fieldName=\"tags\" />\n\n		<@getMetadataField fieldName=\"create-date\" />\n\n		<@getMetadataField fieldName=\"view-count\" />\n\n		<div class=\"asset-content\">\n			<@getSocialBookmarks />\n\n			<div class=\"asset-summary\">\n				<@getMetadataField fieldName=\"author\" />\n\n				${htmlUtil.escape(assetRenderer.getSummary(renderRequest, renderResponse))}\n\n				<a href=\"${viewURL}\"><@liferay.language key=\"read-more\" /><span class=\"hide-accessible\"><@liferay.language key=\"about\" />${entryTitle}</span> &raquo;</a>\n			</div>\n\n			<@getRatings />\n\n			<@getRelatedAssets />\n\n			<@getDiscussion />\n		</div>\n	</div>\n</#list>\n\n<#macro getDiscussion>\n	<#if getterUtil.getBoolean(enableComments) && assetRenderer.isCommentable()>\n		<br />\n\n		<#assign discussionURL = renderResponse.createActionURL() />\n\n		${discussionURL.setParameter(\"javax.portlet.action\", \"invokeTaglibDiscussion\")}\n\n		<@liferay_comment[\"discussion\"]\n			className=entry.getClassName()\n			classPK=entry.getClassPK()\n			formAction=discussionURL?string\n			formName=\"fm\" + entry.getClassPK()\n			ratingsEnabled=getterUtil.getBoolean(enableCommentRatings)\n			redirect=currentURL\n			userId=assetRenderer.getUserId()\n		/>\n	</#if>\n</#macro>\n\n<#macro getEditIcon>\n	<#if assetRenderer.hasEditPermission(themeDisplay.getPermissionChecker())>\n		<#assign editPortletURL = assetRenderer.getURLEdit(renderRequest, renderResponse, windowStateFactory.getWindowState(\"NORMAL\"), themeDisplay.getURLCurrent())!\"\" />\n\n		<#if validator.isNotNull(editPortletURL)>\n			<#assign title = languageUtil.format(locale, \"edit-x\", entryTitle, false) />\n\n			<@liferay_ui[\"icon\"]\n				cssClass=\"icon-monospaced visible-interaction\"\n				icon=\"pencil\"\n				markupView=\"lexicon\"\n				message=title\n				url=editPortletURL.toString()\n			/>\n		</#if>\n	</#if>\n</#macro>\n\n<#macro getFlagsIcon>\n	<#if getterUtil.getBoolean(enableFlags)>\n		<@liferay_flags[\"flags\"]\n			className=entry.getClassName()\n			classPK=entry.getClassPK()\n			contentTitle=entry.getTitle(locale)\n			label=false\n			reportedUserId=entry.getUserId()\n		/>\n	</#if>\n</#macro>\n\n<#macro getMetadataField\n	fieldName\n>\n	<#if stringUtil.split(metadataFields)?seq_contains(fieldName)>\n		<span class=\"metadata-entry metadata-${fieldName}\">\n			<#assign dateFormat = \"dd MMM yyyy - HH:mm:ss\" />\n\n			<#if stringUtil.equals(fieldName, \"author\")>\n				<@liferay.language key=\"by\" /> ${htmlUtil.escape(portalUtil.getUserName(assetRenderer.getUserId(), assetRenderer.getUserName()))}\n			<#elseif stringUtil.equals(fieldName, \"categories\")>\n				<@liferay_asset[\"asset-categories-summary\"]\n					className=entry.getClassName()\n					classPK=entry.getClassPK()\n					portletURL=renderResponse.createRenderURL()\n				/>\n			<#elseif stringUtil.equals(fieldName, \"create-date\")>\n				${dateUtil.getDate(entry.getCreateDate(), dateFormat, locale)}\n			<#elseif stringUtil.equals(fieldName, \"expiration-date\")>\n				${dateUtil.getDate(entry.getExpirationDate(), dateFormat, locale)}\n			<#elseif stringUtil.equals(fieldName, \"modified-date\")>\n				${dateUtil.getDate(entry.getModifiedDate(), dateFormat, locale)}\n			<#elseif stringUtil.equals(fieldName, \"priority\")>\n				${entry.getPriority()}\n			<#elseif stringUtil.equals(fieldName, \"publish-date\")>\n				${dateUtil.getDate(entry.getPublishDate(), dateFormat, locale)}\n			<#elseif stringUtil.equals(fieldName, \"tags\")>\n				<@liferay_asset[\"asset-tags-summary\"]\n					className=entry.getClassName()\n					classPK=entry.getClassPK()\n					portletURL=renderResponse.createRenderURL()\n				/>\n			<#elseif stringUtil.equals(fieldName, \"view-count\")>\n				${entry.getViewCount()} <@liferay.language key=\"views\" />\n			</#if>\n		</span>\n	</#if>\n</#macro>\n\n<#macro getPrintIcon>\n	<#if getterUtil.getBoolean(enablePrint)>\n		<#assign printURL = renderResponse.createRenderURL() />\n\n		${printURL.setParameter(\"mvcPath\", \"/view_content.jsp\")}\n		${printURL.setParameter(\"assetEntryId\", entry.getEntryId()?string)}\n		${printURL.setParameter(\"viewMode\", \"print\")}\n		${printURL.setParameter(\"type\", entry.getAssetRendererFactory().getType())}\n		${printURL.setWindowState(\"pop_up\")}\n\n		<@liferay_ui[\"icon\"]\n			icon=\"print\"\n			markupView=\"lexicon\"\n			message=\"print\"\n			url=\"javascript:Liferay.Util.openWindow({id:\'\" + renderResponse.getNamespace() + \"printAsset\', title: \'\" + languageUtil.format(locale, \"print-x-x\", [\"hide-accessible\", entryTitle], false) + \"\', uri: \'\" + htmlUtil.escapeURL(printURL.toString()) + \"\'});\"\n		/>\n	</#if>\n</#macro>\n\n<#macro getRatings>\n	<#if getterUtil.getBoolean(enableRatings) && assetRenderer.isRatable()>\n		<div class=\"asset-ratings\">\n			<@liferay_ui[\"ratings\"]\n				className=entry.getClassName()\n				classPK=entry.getClassPK()\n			/>\n		</div>\n	</#if>\n</#macro>\n\n<#macro getRelatedAssets>\n	<#if getterUtil.getBoolean(enableRelatedAssets)>\n		<@liferay_asset[\"asset-links\"]\n			assetEntryId=entry.getEntryId()\n			viewInContext=!stringUtil.equals(assetLinkBehavior, \"showFullContent\")\n		/>\n	</#if>\n</#macro>\n\n<#macro getSocialBookmarks>\n	<@liferay_social_bookmarks[\"bookmarks\"]\n		className=entry.getClassName()\n		classPK=entry.getClassPK()\n		displayStyle=\"${socialBookmarksDisplayStyle}\"\n		target=\"_blank\"\n		title=entry.getTitle(locale)\n		types=\"${socialBookmarksTypes}\"\n		url=viewURL\n	/>\n</#macro>',0,20100,NULL,'2020-05-18 08:46:48.791000'),(0,0,34571,20123,20095,20100,NULL,'2020-05-18 08:47:21.120000',34245,34565,34569,'1.0','<?xml version=\'1.0\' encoding=\'UTF-8\'?><root available-locales=\"en_US,sv_SE,pt_BR,ja_JP,fr_FR,hu_HU,de_DE,ca_ES,ar_SA,fi_FI,zh_CN,es_ES,nl_NL\" default-locale=\"en_US\"><Name language-id=\"sv_SE\">Vanligt innehåll</Name><Name language-id=\"pt_BR\">Conteúdo Web básico</Name><Name language-id=\"ja_JP\">基本Webコンテンツ</Name><Name language-id=\"fr_FR\">Contenu web basique</Name><Name language-id=\"hu_HU\">Alapvető webtartalom</Name><Name language-id=\"de_DE\">Einfacher Webcontent</Name><Name language-id=\"ca_ES\">Contigut web bàsic</Name><Name language-id=\"ar_SA\">تحرير محتوى</Name><Name language-id=\"fi_FI\">Tavallinen web-sisältö</Name><Name language-id=\"en_US\">Basic Web Content</Name><Name language-id=\"zh_CN\">基本 Web 内容</Name><Name language-id=\"es_ES\">Contenido web básico</Name><Name language-id=\"nl_NL\">Basiswebcontent</Name></root>','','ftl','${content.getData()}',0,20100,NULL,'2020-05-18 08:47:21.120000');
